  // } catch {
  //   console.log("hhhh");
  // }

  // const asyncFunc = async () => {
  //   console.log(1111);
  // };
  // const syncFunc = () => {
  //   console.log(2222);
  // };

  // asyncFunc();
  // syncFunc();
  const [info, setInfo] = useState([]);
  async function getRickiAndMorty() {
    try {
      let { data } = await axios(API);
      // console.log(data.results);
      const arr = data.results;
      setInfo(arr);
    } catch (err) {
      return err;
    }
  }

  return (
    <>
      <button onClick={getRickiAndMorty}>GET</button>
      <ReadRicki info={info} />
    </>
  );
};

export default App;
